    public void metodoSumarCUBS() {
        try {
            lista = ClaseGeneral.controlCubs.findAllInCubsByFkcontrato(contrato.getText());
            ClaseGeneral.cubs = new Cubs();
            int valorTotalconivaCUBS = 0;
            int valorTotalsinivaCUBS = 0;

            int i = 0;
            for (Object lista1 : lista) {
                ClaseGeneral.cubs = (Cubs) lista1;
                valorTotalconivaCUBS += Integer.parseInt("" + ClaseGeneral.cubs.getValortotal());
                valorTotalsinivaCUBS += Integer.parseInt("" + ClaseGeneral.cubs.getValortotalsiniva());
                i++;
            }

            valortotalconivaCUBS.setText("" + ClaseInformacion.formatoDecimal.format(Long.parseLong("" + valorTotalconivaCUBS)));
            valortotalsinivaCUBS.setText("" + ClaseInformacion.formatoDecimal.format(Long.parseLong("" + valorTotalsinivaCUBS)));
        } catch (Exception ex) {
            Logger.getLogger(PanelContratos.class.getName()).log(Level.SEVERE, null, ex);
        }
    }

    public void metodoCalcularFechaTerminacion() {
        int intPlazo;
        try {
            intPlazo = Integer.parseInt(plazo.getText());
        } catch (Exception ex) {
            plazo.setText("0");
            intPlazo = 0;
        }

        Calendar cal = Calendar.getInstance();
        cal.setTime(fechainicio.getDate());
        if (unidadduracion.getSelectedItem().equals("DÍAS")) {
            cal.add(Calendar.DATE, intPlazo - 1);
        } else if (unidadduracion.getSelectedItem().equals("SEMANAS")) {
            cal.add(Calendar.DATE, 7 * intPlazo);
        } else if (unidadduracion.getSelectedItem().equals("MESES")) {
            cal.add(Calendar.MONTH, intPlazo);
        } else if (unidadduracion.getSelectedItem().equals("AÑOS")) {
            cal.add(Calendar.YEAR, intPlazo);
        }
        fechaterminacion.setDate(new Date(cal.getTimeInMillis()));
    }
